WebSocket类
@ServerEndpoint("/imserver/{user}")
@Slf4j
@Component
public class ImServer {

    private static ConcurrentHashMap sessionMap = new ConcurrentHashMap<>();

    private String user;

    @OnOpen
    public void open(Session session,@PathParam("user") String user){
        log.info(user+"已连接");
        sessionMap.put(user, session);
        this.user = user;
    }

    @OnClose
    public void close(){
       log.info(user + "退出聊天室！");
    }

    @OnMessage
    public void message(String message, Session session){
        log.info(message);
        if(StrUtil.isNotBlank(message)){
            Message msg = new Message();
            msg.setMessage(message);
            msg.setFrom(user);
            msg.setTo("all");
            sendAll(msg);
        }
    }

    @OnError
    public void onError(Session session, Throwable error) {
        log.error("用户错误:"+this.user+",原因:"+error.getMessage());
        error.printStackTrace();
    }
    /**
     * 群发消息
     */
    public static void sendAll(Message message){
        sessionMap.values().forEach(session -> {
            if(session.isOpen()){
                session.getAsyncRemote().sendText(JSONUtil.toJsonStr(message));
            }
        });
    }
}

