chrome.extension.onRequest.addListener(//监听扩展程序进程或内容脚本发送的请求
    function (request, sender, sendResponse) {
        if (request.action == "multi_page") {
            document.getElementById('multi_page').style="width:500px"
        }
        sendResponse({"success":"成功"})
    }
);

